sys_variable_value.value:
(function execute(inputs, outputs) {
  inputs = new sn_jira_spoke.JiraUtils().trimStringInputs(inputs);
  if(gs.nil(inputs.issue_id))
    throw new Error("Issue Id cannot be empty.");
  outputs.issue_id=inputs.issue_id;
})(inputs, outputs);

